document.addEventListener("DOMContentLoaded",()=>{AOS.init({duration:800,offset:100,once:!0,delay:100});const cards=document.querySelectorAll(".unit-card, .resource-card");cards.forEach(card=>{card.addEventListener("mouseenter",()=>{card.style.transform="translateY(-8px)",card.style.boxShadow="0 12px 20px rgba(0, 0, 0, 0.15)"}),card.addEventListener("mouseleave",()=>{card.style.transform="translateY(0)",card.style.boxShadow="0 4px 6px rgba(0, 0, 0, 0.1)"})});const mainContent=document.querySelector("#course-main");if(mainContent){const wordCount=mainContent.textContent.trim().split(/\s+/).length,readingTime=Math.ceil(wordCount/200),timeElement=document.createElement("div");timeElement.className="reading-time",timeElement.innerHTML=`\n            <i class="fas fa-book-reader"></i>\n            <span>Tiempo de lectura: ${readingTime} min</span>\n        `;const courseIntro=document.querySelector(".course-intro");courseIntro.appendChild(timeElement)}});