AOS.init({duration:800,once:!0});const quizData=[{question:"¿Qué es un modelo conceptual en bases de datos?",options:["Una representación física de la base de datos","Una representación abstracta y simplificada de la realidad","Un conjunto de tablas relacionadas","Un diagrama de flujo de datos"],correct:1},{question:"¿Cuál es el propósito principal de la normalización?",options:["Hacer la base de datos más rápida","Reducir la redundancia y dependencias","Crear más tablas","Simplificar las consultas"],correct:1}];function loadQuiz(){const quizForm=document.getElementById("quiz-form");if(!quizForm)return;let quizHTML="";quizData.forEach((q,index)=>{quizHTML+=`\n            <div class="quiz-question">\n                <h4>Pregunta ${index+1}: ${q.question}</h4>\n                <div class="quiz-options">\n                    ${q.options.map((option,optIndex)=>`\n                        <label class="quiz-option">\n                            <input type="radio" name="q${index}" value="${optIndex}">\n                            ${option}\n                        </label>\n                    `).join("")}\n                </div>\n            </div>\n        `}),quizHTML+='<button type="submit" class="submit-quiz">Enviar Respuestas</button>',quizForm.innerHTML=quizHTML,quizForm.addEventListener("submit",handleQuizSubmit)}function handleQuizSubmit(e){e.preventDefault();let score=0;quizData.forEach((q,index)=>{const answer=document.querySelector(`input[name="q${index}"]:checked`);answer&&parseInt(answer.value)===q.correct&&score++}),alert(`Tu puntuación: ${score} de ${quizData.length}`)}function highlightCurrentSection(){const sections=document.querySelectorAll(".content-section"),navLinks=document.querySelectorAll(".unit-nav a");let currentSectionId="";sections.forEach(section=>{const sectionTop=section.offsetTop,sectionHeight=section.clientHeight;window.scrollY>=sectionTop-100&&(currentSectionId=section.id)}),navLinks.forEach(link=>{link.classList.remove("active"),link.getAttribute("href")===`#${currentSectionId}`&&link.classList.add("active")})}document.querySelectorAll(".unit-nav a").forEach(anchor=>{anchor.addEventListener("click",(function(e){e.preventDefault();const targetId=this.getAttribute("href"),targetElement=document.querySelector(targetId);targetElement&&targetElement.scrollIntoView({behavior:"smooth",block:"start"})}))}),window.addEventListener("scroll",highlightCurrentSection),document.addEventListener("DOMContentLoaded",()=>{loadQuiz(),highlightCurrentSection()});